#include <bits/stdc++.h>  using namespace std;  int main() {     set<int> s;     for(int i=0;i<31;i++){         for(int j=0;j<31;j++){             if(i==j)continue;             int x=(1<<i)+(1<<j);             s.insert(x);     }     }     int t;     cin >> t;     while (t--)     {         int n;         cin>>n;         auto upper=s.upper_bound(n);         auto lower=s.lower_bound(n);         upper--;         long long x=min(abs(upper-n),abs(lower-n));         cout<<x<<endl;        } }
